mapper.select:
select * from ( select c.*, rank() over(order by carpool_idx desc) carpool_no, (select count(carpool_idx) from CARPOOL_LIKE where c.carpool_idx=carpool_idx) cnt, (select count(carpool_idx) from CARPOOL_LIKE where c.carpool_idx=carpool_idx and mem_idx=#{m_idx}) heart_flag from (select * from carpool
carpool_title like '%' || #{ carpool_title } || '%'
or carpool_content like '%' || #{ carpool_content } || '%'
or mem_nickname like '%' || #{ mem_nickname } || '%'
) c ) where carpool_no between #{ start } and #{ end }

mapper.update:
update carpool 
   set carpool_hit_count = carpool_hit_count + 1 
where carpool_idx = #{ carpool_idx}
