mapper.select:
select * from ( select r.*, rank() over(order by review_idx desc) review_no from (select * from review
review_title like '%' || #{ review_title } || '%'
or review_content like '%' || #{ review_content } || '%'
or mem_nickname like '%' || #{ mem_nickname } || '%'
) r ) where review_no between #{ start } and #{ end }
